import styled from '@emotion/styled'

function SingleCard ({ post }){
    
    const { API_URL } = process.env;
    const date = post.updated_at;
    return(
        <CardStyled>
            <div className = "body">
                <h2>{ post.post_title }</h2>
                <Author>last updated {date.substring(0,10)} by {post.author}</Author><br/>
                <h3 style = {{fontWeight: "normal", textDecorationColor: "black"}}><p dangerouslySetInnerHTML = {{ __html: post.description }} /></h3>
                <Likes>{post.likes} likes</Likes>
            </div>
        </CardStyled>
    )
}
const Author = styled.p`
    font-style:italic;
`

const Likes = styled.p`
    font-style:italic;
    text-align:center;
`
const CardStyled = styled.div`
    width: 100%;
    border: 1px solid #cccccc;
    margin-top: 50px;
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);

    .body{
        padding: 20px;

        h3{
            margin-bottom: 20px;
        }

        p{
            color: #666666;
            line-height: 1.5;
        }
    }
`
export default SingleCard
