import React, { Component } from 'react'
import { Flex, Box } from 'reflexbox'
import { Label, Input } from '@rebass/forms'
import styled from '@emotion/styled'
import axios from 'axios'
const { API_URL } = process.env


class auth extends Component{
    state = {
        email: '',
        password: ''
    }
    handleChange = (event) => {
        const { name, value } = event.target
        this.setState({[name]: value})
    }
    handleSubmit = async (event) => {
        event.preventDefault();

        const { email, password } = this.state
        
        const data = {
            email,
            password,
            username: email
        }

        const userCreationRes = await axios({
            method: 'POST',
            url: `${API_URL}/auth/local/register`,
            data
        })

        console.log(userCreationRes)
    }
    render(){
    const { email, password } = this.state
    return(
        <Box variant = "formContainer" as = "form" onSubmit = {this.handleSubmit}>
            <Label htmlFor='email'>Email</Label>
                <Input
                    id='email'
                    name='email'
                    type='email'
                    value = {email}
                    onChange = {this.handleChange}
                /><br />
                <Label htmlFor='password'>Password</Label>
                <Input
                    id='password'
                    name='password'
                    type='password'
                    value = {password}
                    onChange = {this.handleChange}
                />
                <ButtonStyled type = "submit" variant = "submitButton">SignUp</ButtonStyled>
        </Box>
    )
    }
}

const ButtonStyled = styled.button`
    border: 1px solid black;
    height: 30px;
    width: 70px;
    font-weight: bold;
    margin-top: 20px;
`
export default auth
