import styled from '@emotion/styled'
import Link from 'next/link'

function Card ({ post }){
    
    const { API_URL } = process.env;

    return(
        <CardStyled>
            <div className = "body">
                <h3>            <Link href = {`/post/${post.id}`}>
{ post.post_title }</Link></h3>
            
                <p dangerouslySetInnerHTML = {{ __html: post.description }} />
                <Likes>{post.likes} likes</Likes>
            </div>
        </CardStyled>
    )
}

const Likes = styled.p`
    font-style:italic;
    text-align:center;
`
const CardStyled = styled.div`
    width: 100%;
    border: 1px solid #cccccc;
    margin-top: 50px;
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);

    .body{
        padding: 20px;

        h3{
            margin-bottom: 20px;
        }

        p{
            color: #666666;
            line-height: 1.5;
        }
    }
`
export default Card
